import { Component } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import firebase from 'firebase/compat/app';
import { AngularFireAuth } from '@angular/fire/compat/auth'; // ‚Üê Make sure this is imported


export interface Expense extends firebase.firestore.DocumentData {
  id?: string;
  date: any;
  amount: number;
  category: string;
  description: string;
  method: string;
  location: string;
}

@Component({
  selector: 'app-expenses',
  templateUrl: './expenses.component.html',
  styleUrls: ['./expenses.component.scss']
})
export class ExpensesComponent {
  displayedColumns: string[] = ['date', 'amount', 'category', 'description', 'method', 'location', 'actions'];
  expenseList: Expense[] = [];
  dataSource = new MatTableDataSource<Expense>([]);

  newExpense: Expense = {
    date: '',
    amount: 0,
    category: '',
    description: '',
    method: '',
    location: ''
  };

  editMode = false;
  editingExpenseId: string | null = null;
  showForm = false;

  constructor(private firestore: AngularFirestore, private auth: AngularFireAuth) {   this.loadExpenses(); }

  // üîÅ Load all expenses
  loadExpenses() {
    this.auth.authState.subscribe(user => {
      if (!user) {
        this.expenseList = [];
        this.dataSource.data = [];
        return;
      }

      this.firestore.collection<Expense>('expenses', ref =>
        ref.where('userId', '==', user.uid).orderBy('date', 'desc')
      ).valueChanges({ idField: 'id' }).subscribe((data: Expense[]) => {
        this.expenseList = data;
        this.dataSource.data = this.expenseList;
      });
    });
  }


  // üîç Search/filter table
  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value.trim().toLowerCase();
    this.dataSource.filter = filterValue;
  }

  // ‚ûï Open form for new expense
  toggleForm() {
    this.showForm = !this.showForm;
    if (!this.showForm) {
      this.resetForm();
    }
  }

  // üßπ Reset form fields
  resetForm() {
    this.newExpense = {
      date: '',
      amount: 0,
      category: '',
      description: '',
      method: '',
      location: ''
    };
    this.editMode = false;
    this.editingExpenseId = null;
  }

  // ‚úèÔ∏è Load selected row into form for editing
  startEdit(expense: Expense) {
    this.newExpense = {
      ...expense,
      date: this.formatDate(expense.date)
    };
    this.editingExpenseId = expense.id ?? null;
    this.editMode = true;
    this.showForm = true;
  }

  // ‚úÖ Add or Update expense
  async submitExpense() {
    const { date, amount, category, description, method, location } = this.newExpense;

    if (date && amount && category && description && method && location) {
      const user = firebase.auth().currentUser;
      if (!user) {
        alert('You must be logged in to add expenses.');
        return;
      }

      const payload = {
        date: new Date(date + 'T12:00:00'),
        amount,
        category,
        description,
        method,
        location,
        userId: user.uid
      };

      if (this.editMode && this.editingExpenseId) {
        await this.firestore.collection('expenses').doc(this.editingExpenseId).update(payload);
      } else {
        await this.firestore.collection('expenses').add(payload);
      }

      this.resetForm();
      this.showForm = false;

      // ‚úÖ Load latest expenses again
      this.loadExpenses();
    } else {
      alert('Please fill in all fields.');
    }
  }



  // ‚ùå Cancel edit or form
  cancelEdit() {
    this.resetForm();
    this.showForm = false;
  }


  // üóëÔ∏è Delete a record
  deleteExpense(id: string) {
    this.firestore.doc(`expenses/${id}`).delete();
  }

  // üìÖ Format date for input compatibility
  formatDate(date: any): string {
    if (date?.toDate) {
      return new Date(date.toDate()).toISOString().substring(0, 10);
    } else if (typeof date === 'string' || date instanceof Date) {
      return new Date(date).toISOString().substring(0, 10);
    }
    return '';
  }
}
<div class="expenses-container">
  <h2>Add / Remove Expenses</h2>

  <!-- Top Control Bar -->
  <div class="top-controls">
    <mat-form-field appearance="outline" class="search-box">
      <mat-label>Search Transactions</mat-label>
      <input matInput (keyup)="applyFilter($event)" placeholder="Enter text to filter..." />
    </mat-form-field>

    <button mat-raised-button color="primary" (click)="toggleForm()">Add Expense</button>
  </div>

  <!-- Add/Edit Form (conditional) -->
  <form class="expense-form" *ngIf="showForm" (ngSubmit)="submitExpense()" #expenseForm="ngForm">
    <mat-form-field appearance="outline">
      <mat-label>Date</mat-label>
      <input matInput type="date" [(ngModel)]="newExpense.date" name="date" required />
    </mat-form-field>

    <mat-form-field appearance="outline">
      <mat-label>Amount</mat-label>
      <input matInput type="number" [(ngModel)]="newExpense.amount" name="amount" required />
    </mat-form-field>

    <mat-form-field appearance="outline">
      <mat-label>Category</mat-label>
      <mat-select [(ngModel)]="newExpense.category" name="category" required>
        <mat-option value="Food">Food</mat-option>
        <mat-option value="Groceries">Groceries</mat-option>
        <mat-option value="Entertainment">Entertainment</mat-option>
        <mat-option value="Utilities">Utilities</mat-option>
        <mat-option value="income">Income</mat-option>
      </mat-select>
    </mat-form-field>

    <mat-form-field appearance="outline">
      <mat-label>Description</mat-label>
      <input matInput [(ngModel)]="newExpense.description" name="description" required />
    </mat-form-field>

    <mat-form-field appearance="outline">
      <mat-label>Payment Method</mat-label>
      <mat-select [(ngModel)]="newExpense.method" name="method" required>
        <mat-option value="Cash">Cash</mat-option>
        <mat-option value="UPI">UPI</mat-option>
        <mat-option value="Credit Card">Credit Card</mat-option>
      </mat-select>
    </mat-form-field>

    <mat-form-field appearance="outline">
      <mat-label>Location</mat-label>
      <input matInput [(ngModel)]="newExpense.location" name="location" required />
    </mat-form-field>

    <!-- Buttons -->
    <div class="form-buttons">
      <button mat-raised-button color="primary" type="submit">
        {{ editMode ? 'Update Expense' : 'Submit Expense' }}
      </button>
      <button mat-stroked-button color="warn" type="button" (click)="cancelEdit()">Cancel</button>
    </div>
  </form>

  <!-- Table -->
  <div class="table-wrapper">
  <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">
    <ng-container matColumnDef="date">
      <th mat-header-cell *matHeaderCellDef>Date</th>
      <td mat-cell *matCellDef="let el">{{ formatDate(el.date) }}</td>
    </ng-container>

    <ng-container matColumnDef="amount">
      <th mat-header-cell *matHeaderCellDef>Amount</th>
      <td mat-cell *matCellDef="let el">{{ el.amount | currency: 'USD' }}</td>
    </ng-container>

    <ng-container matColumnDef="category">
      <th mat-header-cell *matHeaderCellDef>Category</th>
      <td mat-cell *matCellDef="let el">{{ el.category }}</td>
    </ng-container>

    <ng-container matColumnDef="description">
      <th mat-header-cell *matHeaderCellDef>Description</th>
      <td mat-cell *matCellDef="let el">{{ el.description }}</td>
    </ng-container>

    <ng-container matColumnDef="method">
      <th mat-header-cell *matHeaderCellDef>Method</th>
      <td mat-cell *matCellDef="let el">{{ el.method }}</td>
    </ng-container>

    <ng-container matColumnDef="location">
      <th mat-header-cell *matHeaderCellDef>Location</th>
      <td mat-cell *matCellDef="let el">{{ el.location }}</td>
    </ng-container>

    <ng-container matColumnDef="actions">
      <th mat-header-cell *matHeaderCellDef>Actions</th>
      <td mat-cell *matCellDef="let el">
        <button mat-icon-button color="primary" (click)="startEdit(el)">
          <mat-icon>edit</mat-icon>
        </button>
        <button mat-icon-button color="warn" (click)="deleteExpense(el.id)">
          <mat-icon>delete</mat-icon>
        </button>
      </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
  </div>
</div>
