import { Component } from '@angular/core';

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.scss']
})
export class AboutComponent {

}
<div class="about-container">
  <div class="about-card">
    <h1 class="glow-heading">Welcome to Expense Tracker 💸</h1>
    <p class="subtitle">
      Track your <strong>income</strong>, <strong>expenses</strong>, and <strong>savings</strong> effortlessly.
      Visualize trends, monitor monthly insights, and take control of your financial future.
    </p>
    <p class="tagline">Start managing your money smarter today!</p>

    <button class="get-started-button" (click)="goHome()">Get Started</button>
  </div>
</div>
