// add-bill-dialog.component.ts
import { Component } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog';

@Component({
  selector: 'app-add-bill-dialog',
  templateUrl: './add-bill-dialog.component.html',
  styleUrls: ['./add-bill-dialog.component.scss']
})
export class AddBillDialogComponent {
  bill = {
    type: 'regular',
    name: '',
    amount: 0,
    dueDate: '',
    category: '',
    monthlyPayment: 0
  };

  get installmentInfo(): string {
    if (['regular', 'credit'].includes(this.bill.type) && this.bill.amount > 0 && this.bill.monthlyPayment > 0) {
      const totalMonths = Math.ceil(this.bill.amount / this.bill.monthlyPayment);
      return `0 of ${totalMonths} installments`;
    }
    return '';
  }

  constructor(private dialogRef: MatDialogRef<AddBillDialogComponent>) {}

  close(): void {
    this.dialogRef.close();
  }

  save(): void {
    this.dialogRef.close(this.bill);
  }
}
<h2 class="dialog-title">Add New Bill</h2>
<div class="form-group">
  <label>Bill Type</label>
  <select [(ngModel)]="bill.type">
    <option value="regular">Regular Bill</option>
    <option value="credit">Credit Card</option>
    <option value="loan">Personal Loan</option>
  </select>
</div>
<div class="form-group">
  <label>Name</label>
  <input type="text" [(ngModel)]="bill.name" placeholder="Enter bill name" />
</div>
<div class="form-group">
  <label>Amount</label>
  <input type="number" [(ngModel)]="bill.amount" />
</div>
<div class="form-group" *ngIf="bill.type === 'regular' || bill.type === 'credit' || bill.type === 'loan'">
  <label>Monthly Payment <span *ngIf="bill.type !== 'loan'">*</span></label>
  <input type="number" [(ngModel)]="bill.monthlyPayment" />
</div>
<p class="installment-info" *ngIf="installmentInfo">{{ installmentInfo }}</p>
<div class="form-group">
  <label>Due Date</label>
  <input [matDatepicker]="picker" matInput [(ngModel)]="bill.dueDate" />
  <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
  <mat-datepicker #picker></mat-datepicker>
</div>
<div class="form-group">
  <label>Category</label>
  <input type="text" [(ngModel)]="bill.category" />
</div>
<div class="dialog-actions">
  <button class="cancel-btn" (click)="close()">Cancel</button>
  <button class="add-btn" (click)="save()">Add Bill</button>
</div>
