import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import firebase from 'firebase/compat/app'; // Firebase compat
import { AngularFireAuth } from '@angular/fire/compat/auth';
import { AuthService } from 'src/app/services/auth.service';
import { MatDialog } from '@angular/material/dialog';
import { ChangePasswordDialogComponent } from 'src/app/pages/ChangePasswordDialogComponent/change-password-dialog.component';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent implements OnInit {
  user: firebase.User | null = null;
  isMobile: boolean = false;
  showMobileMenu: boolean = false;

  constructor(
    private authService: AuthService,
    private afAuth: AngularFireAuth,
    private router: Router,
    private dialog: MatDialog
  ) {}

  ngOnInit() {
    this.authService.getCurrentUser().subscribe(user => {
      this.user = user;
    });

    this.checkIfMobile();
    window.addEventListener('resize', this.checkIfMobile.bind(this));
  }

  checkIfMobile() {
    this.isMobile = window.innerWidth <= 768;
  }

  toggleMobileMenu() {
    this.showMobileMenu = !this.showMobileMenu;
  }

  logout() {
    this.authService.logout().then(() => {
      this.router.navigate(['/auth']);
    });
  }

  openChangePasswordDialog() {
    this.dialog.open(ChangePasswordDialogComponent, {
      width: '400px'
    });
  }
}
<mat-toolbar class="navbar">
  <div class="logo-section">
    <span class="logo">💰 Expense Tracker</span>
    <button *ngIf="isMobile" class="menu-toggle" (click)="toggleMobileMenu()" aria-label="Menu">
      <mat-icon>menu</mat-icon>
    </button>

  </div>

  <!-- Desktop Navigation -->
  <nav class="nav-links" *ngIf="!isMobile">
    <a mat-button routerLink="/about" routerLinkActive="active-link">About</a>
    <a mat-button routerLink="/home" routerLinkActive="active-link">Home</a>
    <a mat-button routerLink="/monthly" routerLinkActive="active-link">Monthly</a>
    <a mat-button routerLink="/expenses" routerLinkActive="active-link">Add/Remove</a>
    <a mat-button routerLink="/bills" routerLinkActive="active-link">Bills</a>
  </nav>

  <!-- Mobile Menu -->
  <div *ngIf="isMobile && showMobileMenu" class="mobile-nav-menu">
    <a routerLink="/about" routerLinkActive="active-link" (click)="toggleMobileMenu()">About</a>
    <a routerLink="/home" routerLinkActive="active-link" (click)="toggleMobileMenu()">Home</a>
    <a routerLink="/monthly" routerLinkActive="active-link" (click)="toggleMobileMenu()">Monthly</a>
    <a routerLink="/expenses" routerLinkActive="active-link" (click)="toggleMobileMenu()">Add/Remove</a>
    <a routerLink="/bills" routerLinkActive="active-link" (click)="toggleMobileMenu()">Bills</a>
  </div>

  <!-- User Profile Menu -->
  <ng-container *ngIf="user as currentUser">
    <button mat-icon-button [matMenuTriggerFor]="userMenu" class="user-button">
      <mat-icon>account_circle</mat-icon>
    </button>

    <mat-menu #userMenu="matMenu">
      <div class="user-info">
        <p><strong>{{ currentUser.displayName }}</strong></p>
        <p>{{ currentUser.email }}</p>
        <p *ngIf="currentUser.photoURL">📞 {{ currentUser.photoURL }}</p>
      </div>
      <button mat-menu-item (click)="openChangePasswordDialog()">
        <mat-icon>lock</mat-icon>
        <span>Change Password</span>
      </button>
      <mat-divider></mat-divider>
      <button mat-menu-item (click)="logout()">
        <mat-icon>logout</mat-icon>
        <span>Logout</span>
      </button>
    </mat-menu>
  </ng-container>
</mat-toolbar>
