import { Component, OnInit, OnChanges, SimpleChanges, ViewChild } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { ChartConfiguration, ChartOptions, Chart } from 'chart.js';
import { Expense } from '../expenses/expenses.component';
import ChartDataLabels from 'chartjs-plugin-datalabels';

Chart.register(ChartDataLabels);

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit, OnChanges {
  totalReceived = 0;
  totalSpent = 0;
  totalSaved = 0;
  totalCardAmount = 0;

  lineChartData: ChartConfiguration<'line'>['data'] = {
    labels: [],
    datasets: [
      {
        data: [],
        label: 'Savings',
        fill: true,
        tension: 0.4,
        borderColor: '#4caf50',
        backgroundColor: 'rgba(76, 175, 80, 0.2)'
      }
    ]
  };

  // üëá Updated pie chart to doughnut chart
  pieChartData: ChartConfiguration<'doughnut'>['data'] = {
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: ['#42a5f5', '#66bb6a', '#ffa726', '#ef5350', '#ab47bc', '#26c6da']
      }
    ]
  };

  barChartData: ChartConfiguration<'bar'>['data'] = {
    labels: [],
    datasets: [
      {
        data: [],
        label: 'Spent',
        backgroundColor: '#ff7043',
        barPercentage: 0.4,
        categoryPercentage: 0.6
      }
    ]
  };

  @ViewChild('pieChart') pieChart: any;
  @ViewChild('barChart') barChart: any;
  @ViewChild('lineChart') lineChart: any;

@ViewChild('creditChart') creditChart: any;

  constructor(private firestore: AngularFirestore) {}

  ngOnInit() {
    this.loadData();
    this.loadCardData();

  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes['pieChartData']) {
      this.updateCharts();
    }
  }

  loadData() {
    this.firestore.collection<Expense>('expenses').valueChanges().subscribe((data: Expense[]) => {
      let received = 0;
      let spent = 0;

      const monthlyMap: { [key: string]: { received: number; spent: number } } = {};
      const categoryMap: { [category: string]: number } = {};

      data.forEach(exp => {
        const date = exp.date?.toDate ? exp.date.toDate() : new Date(exp.date);
        if (isNaN(date.getTime())) return;

        const monthKey = `${date.toLocaleString('default', { month: 'short' })} ${date.getFullYear()}`;
        const isIncome = exp.category.toLowerCase() === 'income';

        if (!monthlyMap[monthKey]) {
          monthlyMap[monthKey] = { received: 0, spent: 0 };
        }

        if (isIncome) {
          received += exp.amount;
          monthlyMap[monthKey].received += exp.amount;
        } else {
          spent += exp.amount;
          monthlyMap[monthKey].spent += exp.amount;

          if (!categoryMap[exp.category]) categoryMap[exp.category] = 0;
          categoryMap[exp.category] += exp.amount;
        }
      });

      this.totalReceived = received;
      this.totalSpent = spent;
      this.totalSaved = received - spent;

      const monthLabels = Object.keys(monthlyMap);
      const savingsData = monthLabels.map(key => monthlyMap[key].received - monthlyMap[key].spent);
      const spentData = monthLabels.map(key => monthlyMap[key].spent);
      const pieLabels = Object.keys(categoryMap);
      const pieData = pieLabels.map(key => categoryMap[key]);

      this.lineChartData.labels = monthLabels;
      this.lineChartData.datasets[0].data = savingsData;

      this.barChartData.labels = monthLabels;
      this.barChartData.datasets[0].data = spentData;

      this.pieChartData.labels = pieLabels;
      this.pieChartData.datasets[0].data = pieData;

      this.updateCharts();
    });
  }

  updateCharts() {
    if (this.pieChart && !this.pieChart.chart) {
      this.pieChart.chart = new Chart(this.pieChart.nativeElement, {
        type: 'doughnut', // üëà Changed from 'pie'
        data: this.pieChartData,
        options: this.pieChartOptions
      });
    }
    if (this.barChart && !this.barChart.chart) {
      this.barChart.chart = new Chart(this.barChart.nativeElement, {
        type: 'bar',
        data: this.barChartData,
        options: this.barChartOptions
      });
    }
    if (this.lineChart && !this.lineChart.chart) {
      this.lineChart.chart = new Chart(this.lineChart.nativeElement, {
        type: 'line',
        data: this.lineChartData,
        options: this.lineChartOptions
      });
    }
  }

  // üëá Updated type from 'pie' to 'doughnut'
  pieChartOptions: ChartOptions<'doughnut'> = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      datalabels: {
        color: '#fff',
        formatter: (value: number, ctx) => {
          const data = ctx.chart.data.datasets[0].data as number[];
          const total = data.reduce((a, b) => a + b, 0);
          return total ? ((value / total) * 100).toFixed(0) + '%' : '';
        },
        font: { weight: 'bold' as const, size: 14 }
      }
    },
    cutout: '60%' // üç© thickness of donut hole
  };

  pieChartPlugins = [ChartDataLabels];

  lineChartOptions: ChartConfiguration<'line'>['options'] = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { position: 'top' }
    },
    layout: { padding: { top: 10, bottom: 10 } }
  };

  barChartOptions: ChartConfiguration<'bar'>['options'] = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { position: 'top' }
    },
    scales: {
      x: {
        ticks: { font: { size: 12 } },
        grid: { display: false }
      },
      y: {
        beginAtZero: true,
        ticks: { font: { size: 12 } },
        grid: { color: 'rgba(0, 0, 0, 0.05)' }
      }
    }
  };

creditChartData: ChartConfiguration<'line'>['data'] = {
  labels: [],
  datasets: [{
    data: [],
    label: 'Card Balance',
    fill: true,
    tension: 0.4,
    borderColor: '#ab47bc',
    backgroundColor: 'rgba(171, 71, 188, 0.2)',
    pointRadius: 4,
    pointHoverRadius: 6
  }]
};

creditChartOptions: ChartConfiguration<'line'>['options'] = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: { position: 'top' }
  },
  layout: { padding: { top: 10, bottom: 10 } }
};
loadCardData() {
  const bills = JSON.parse(localStorage.getItem('bills') || '[]');
  const creditBills = bills.filter((b: any) => b.type === 'credit');

  this.totalCardAmount = creditBills.reduce((sum: number, b: any) => sum + b.amount, 0);

  const progressData = creditBills.map((b: any) => {
    const base = b.amount;
    const paid = (b.paidCount || 0) * (b.monthlyPayment || 0);
    return base - paid;
  });

  const labels = creditBills.map((b: any, index: number) =>
    `Month ${index + 1}`
  );

  this.creditChartData.labels = labels;
  this.creditChartData.datasets[0].data = progressData;

  if (this.creditChart && !this.creditChart.chart) {
    this.creditChart.chart = new Chart(this.creditChart.nativeElement, {
      type: 'line',
      data: this.creditChartData,
      options: this.creditChartOptions
    });
  }
}

}


<div class="dashboard-container">
  <!-- Summary Cards -->
  <div class="summary-cards">
    <mat-card class="card received">
      <span class="cardname">Total Received:</span>
      <span class="currency">{{ totalReceived | currency: 'USD' }}</span>
     </mat-card>
    <mat-card class="card spent">
      <span class="cardname">Total Spent:</span>
      <span class="currency">{{ totalSpent | currency: 'USD' }}</span> </mat-card>
    <mat-card class="card saved">
      <span class="cardname">Total Saved:</span> 
      <span class="currency">{{ totalSaved | currency: 'USD' }}</span></mat-card>
  </div>
  <mat-card class="card credit">
    <span class="cardname">Total Card Amount:</span>
    <span class="currency">{{ totalCardAmount | currency: 'USD' }}</span>
  </mat-card>

  <!-- Charts Row 1: Line + Pie -->
  <div class="top-row">
    <div class="chart">
      <h3>Savings Growth</h3>
      <canvas #lineChart></canvas> <!-- Updated with ViewChild reference -->
    </div>

    <div class="chart">
      <h3>Spending by Category</h3>
      <canvas #pieChart></canvas> <!-- Updated with ViewChild reference -->
    </div>
  </div>

  <!-- Bar Chart Row -->
  <div class="bottom-row">
    <div class="chart bar-chart">
      <h3>Monthly Spend</h3>
      <canvas #barChart></canvas> <!-- Updated with ViewChild reference -->
    </div>
    <div class="chart">
      <h3>Credit Card Balance Over Time</h3>
      <canvas #creditChart></canvas>
    </div>

  </div>
</div>
