import { Component } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog';

@Component({
  selector: 'app-add-bill-dialog',
  templateUrl: './add-bill-dialog.component.html',
  styleUrls: ['./add-bill-dialog.component.scss']
})
export class AddBillDialogComponent {
  bill = {
    type: 'regular',
    name: '',
    amount: null as number | null,
    dueDate: null as Date | null,
    autoDueDate: false,
    category: '',
    monthlyPayment: null as number | null,
    noMonthlyPayment: false,
    autoCalculateDueDate: false
  };

  constructor(private dialogRef: MatDialogRef<AddBillDialogComponent>) {}

  close(): void {
    this.dialogRef.close();
  }

  addBill(): void {
    // Auto-calculate due date if checkbox is selected
    if (this.bill.autoDueDate && this.calculatedDueDate) {
      this.bill.dueDate = this.calculatedDueDate;
    }
    this.dialogRef.close(this.bill);
  }

  get installmentInfo(): string {
    if (
      ['regular', 'credit'].includes(this.bill.type) &&
      this.bill.amount !== null &&
      this.bill.monthlyPayment !== null &&
      this.bill.amount > 0 &&
      this.bill.monthlyPayment > 0 &&
      !this.bill.noMonthlyPayment
    ) {
      const totalMonths = Math.ceil(this.bill.amount / this.bill.monthlyPayment);
      return `0 of ${totalMonths} installments`;
    }
    return '';
  }

  get calculatedInstallments(): number | null {
    if (
      this.bill.amount > 0 &&
      this.bill.monthlyPayment !== null &&
      this.bill.monthlyPayment > 0 &&
      !this.bill.noMonthlyPayment
    ) {
      return Math.ceil(this.bill.amount / this.bill.monthlyPayment);
    }
    return null;
  }

  get calculatedDueDate(): Date | null {
    if (this.bill.autoDueDate && this.calculatedInstallments) {
      const due = new Date();
      due.setMonth(due.getMonth() + this.calculatedInstallments);
      return due;
    }
    return null;
  }

}
<h2 mat-dialog-title>Add New Bill</h2>
<div mat-dialog-content class="form-container">
  <mat-form-field appearance="outline" class="full-width">
    <mat-label>Bill Type</mat-label>
    <mat-select [(value)]="bill.type">
      <mat-option value="regular">Regular Bill</mat-option>
      <mat-option value="credit">Credit Card</mat-option>
      <mat-option value="loan">Personal Loan</mat-option>
    </mat-select>
  </mat-form-field>

  <mat-form-field appearance="outline" class="full-width">
    <mat-label>Name</mat-label>
    <input matInput placeholder="Enter bill name" [(ngModel)]="bill.name" name="name" />
  </mat-form-field>

  <mat-form-field appearance="outline" class="full-width">
    <mat-label>Amount</mat-label>
    <input 
      matInput 
      type="number" 
      [(ngModel)]="bill.amount" 
      (focus)="bill.amount === 0 && (bill.amount = null)" 
    />
  </mat-form-field>

  <!-- Monthly Payment (shown for all types) -->
  <mat-form-field appearance="outline" class="full-width" *ngIf="bill.type !== 'loan' || !bill.noMonthlyPayment">
    <mat-label>Monthly Payment</mat-label>
    <input
      matInput
      type="number"
      [(ngModel)]="bill.monthlyPayment"
      [disabled]="bill.type === 'loan' && bill.noMonthlyPayment"
      (focus)="bill.monthlyPayment === 0 && (bill.monthlyPayment = null)"
    />
  </mat-form-field>

  <!-- Optional Checkbox for Personal Loans -->
  <mat-checkbox *ngIf="bill.type === 'loan'" [(ngModel)]="bill.noMonthlyPayment">
    Not Applicable (No Monthly Payment)
  </mat-checkbox>

  <!-- Due Date Field (conditionally shown) -->
  <mat-form-field appearance="outline" class="full-width" *ngIf="!bill.autoDueDate">
    <mat-label>Due Date</mat-label>
    <input matInput [matDatepicker]="picker" [(ngModel)]="bill.dueDate" />
    <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
    <mat-datepicker #picker></mat-datepicker>
  </mat-form-field>

  <!-- Auto Due Date Checkbox -->
  <mat-checkbox [(ngModel)]="bill.autoDueDate">
    Auto-calculate due date from monthly payment
  </mat-checkbox>

  <!-- Auto Calculation Preview -->
  <p *ngIf="bill.autoDueDate && calculatedDueDate">
    📅 Estimated completion: <strong>{{ calculatedDueDate | date:'longDate' }}</strong>
  </p>

  <mat-form-field appearance="outline" class="full-width">
    <mat-label>Category</mat-label>
    <input matInput [(ngModel)]="bill.category" />
  </mat-form-field>
</div>

<div mat-dialog-actions align="end">
  <button mat-button mat-dialog-close>Cancel</button>
  <button mat-flat-button color="primary" (click)="addBill()">Add Bill</button>
</div>
