import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { AddBillDialogComponent } from '../add-bill-dialog/add-bill-dialog.component';

@Component({
  selector: 'app-bills',
  templateUrl: './bills.component.html',
  styleUrls: ['./bills.component.scss'],
})
export class BillsComponent {
  selectedBillType = 'credit';
  weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  personalLoanAmount: number = 0;
  allBills: any[] = []; // Store all bills
  regularBills: any[] = [];

  constructor(private dialog: MatDialog) {}

  openAddBillDialog(): void {
    const dialogRef = this.dialog.open(AddBillDialogComponent, {
      width: '400px',
      panelClass: 'custom-dialog-container',
    });

    dialogRef.afterClosed().subscribe((result) => {
      if (result) {
        if (result.type === 'regular') {
          // calculate installments if monthlyPayment is provided
          result.installments = result.monthlyPayment && result.monthlyPayment > 0
            ? Math.ceil(result.amount / result.monthlyPayment)
            : null;
          this.regularBills.push(result);
        }
        // You can do the same logic for 'credit' and 'loan'
      }
    });
  }


  // Categorized filters for display
  get creditBills() {
    return this.allBills.filter(bill => bill.type === 'credit');
  }

  get loanBills() {
    return this.allBills.filter(bill => bill.type === 'loan');
  }
  getFormattedDate(dateStr: string): string {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  }

  getTotalInstallments(bill: any): number {
    if (bill.amount && bill.monthlyPayment && bill.monthlyPayment > 0) {
      return Math.ceil(bill.amount / bill.monthlyPayment);
    }
    return 1;
  }

  markInstallmentPaid(bill: any): void {
    bill.paidCount = (bill.paidCount || 0) + 1;
  }

}
