import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import firebase from 'firebase/compat/app'; // âœ… use compat type
import { AngularFireAuth } from '@angular/fire/compat/auth';
import { AuthService } from 'src/app/services/auth.service';
import { MatDialog } from '@angular/material/dialog';
import { ChangePasswordDialogComponent } from 'src/app/pages/ChangePasswordDialogComponent/change-password-dialog.component';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent implements OnInit {
  user: firebase.User | null = null; // âœ… use compat type

  constructor(
    private authService: AuthService,
    private afAuth: AngularFireAuth,
    private router: Router,
    private dialog: MatDialog
  ) {}

  ngOnInit() {
    this.authService.getCurrentUser().subscribe(user => {
      this.user = user;
    });
  }

  logout() {
    this.authService.logout().then(() => {
      this.router.navigate(['/auth']);
    });
  }
  openChangePasswordDialog() {
    this.dialog.open(ChangePasswordDialogComponent, {
      width: '400px'
    });
  }
}
<mat-toolbar class="navbar">
  <span class="logo">ðŸ’° Expense Tracker</span>
  <span class="spacer"></span>

  <a mat-button routerLink="/about">About</a>
  <a mat-button routerLink="/home">Home</a>
  <a mat-button routerLink="/monthly">Monthly</a>
  <a mat-button routerLink="/expenses">Add/Remove</a>

  <!-- ðŸ‘¤ Profile Dropdown -->
  <ng-container *ngIf="user">
    <button mat-icon-button [matMenuTriggerFor]="userMenu">
      <mat-icon>account_circle</mat-icon>
    </button>
    <mat-menu #userMenu="matMenu">
      <div class="user-info">
        <p><strong>{{ user.displayName }}</strong></p>
        <p>{{ user.email }}</p>
        <p *ngIf="user.photoURL">ðŸ“ž {{ user.photoURL }}</p>
      </div>
      <button mat-menu-item (click)="logout()">
        <mat-icon>logout</mat-icon>
        <span>Logout</span>
      </button>
    </mat-menu>
  </ng-container>
</mat-toolbar>
